mapper.update:

update rf_activation_code
set  pad_type = #{padType,jdbcType=VARCHAR},
  pad_time = #{padTime,jdbcType=INTEGER}
where type_ID = #{typeId,jdbcType=INTEGER} and activation_status = #{activationStatus,jdbcType=VARCHAR}

mapper.select:

select 

  ${one}

from rf_activation_code

  


  order by ${example.orderByClause}

offset 0 limit 1
