mapper.select:

select 

  ${one}

from rf_pad_grant

  


  order by ${example.orderByClause}

offset 0 limit 1

  	
  	select count(grant_id) from rf_pad_grant where 1 = 1  
		
			and user_pad_id = #{userPadId}		
		
		
		
			and status = #{status}		
		
		
		
			and enable_status = #{enableStatus}		
		
		
		
			and grant_type = #{grantType}

mapper.insert:
		
	      	select nextval('seq_rf_pad_grant')
	    
		
		    INSERT INTO rf_pad_grant
		      (
		      grant_id,
		      user_pad_id,
		      user_id,
		      grantor_user_id,
		      pad_id,
		      grant_code_id,
		      grant_mode,
		      grant_control,
		      grant_watch,
		      grant_start_time,
		      grant_end_time,
		      grant_time,
		      status,
		      enable_status,
		      creater,
		      create_time,
		      modifier,
		      modify_time,
		      reorder,
		      remark,
		      grant_type
		      )
		    select
					#{grantId,jdbcType=INTEGER}
				,
					#{userPadId,jdbcType=INTEGER}
				,
					#{userId,jdbcType=INTEGER}
				,
					#{grantorUserId,jdbcType=INTEGER}
				,
					#{padId,jdbcType=INTEGER}
				,
					#{grantCodeId,jdbcType=INTEGER}
				,
					#{grantMode,jdbcType=VARCHAR}
				,
					#{grantControl,jdbcType=VARCHAR}
				,
					#{grantWatch,jdbcType=VARCHAR}
				,
					#{grantStartTime,jdbcType=TIMESTAMP}
				,
					#{grantEndTime,jdbcType=TIMESTAMP}
				,
					#{grantTime,jdbcType=TIMESTAMP}
				,
					#{status,jdbcType=VARCHAR}
				,
					#{enableStatus,jdbcType=VARCHAR}
				,
					#{creater,jdbcType=VARCHAR}
				,
					#{createTime,jdbcType=TIMESTAMP}
				,
					#{modifier,jdbcType=VARCHAR}
				,
					#{modifyTime,jdbcType=TIMESTAMP}
				,
					#{reorder,jdbcType=INTEGER}
				,
					#{remark,jdbcType=VARCHAR}
				,
					#{grantType,jdbcType=VARCHAR}	
				where not exists (select 1 from rf_pad_grant where user_pad_id = #{userPadId,jdbcType=INTEGER} and grant_type = '0' and status='1' and enable_status='1')

mapper.select:
  	select count(grant_id) from rf_pad_grant where 1=1
  	
			and user_id = #{userId}		
	
  	
			and pad_id = #{padId}
