mapper.select:

select count(*) from rf_task_record where 1=1 
 AND user_id = #{userId} 
 AND task_id IN #{item}
 AND invite_code = #{inviteCode} 
 AND create_time >= #{createTimeGte} 
 AND create_time <= #{createTimeLte} 
 AND status = #{status}


select 

  ${one}

from rf_task_record

  


  order by ${example.orderByClause}

limit 1
