where.if:
     	and exists (select notice.notice_id from rf_notice as notice where notice.notice_id = rf_user_notice.notice_id 
    		
		  		  and notice.notice_title LIKE  '%' || #{map.noticeTitle} || '%'
		  	
		  	
		   		  and notice.notice_content LIKE  '%' || #{map.noticeContent} || '%'
		  	
		  	
		   		and notice.pop_status = #{map.popStatus,jdbcType=VARCHAR} 
		  	
		  	
		   		and notice.status = #{map.status,jdbcType=VARCHAR} 
		  	
		  	
