     	 )

mapper.select:

select 

  ${one}

from rf_user_notice

  


  order by ${example.orderByClause}

offset 0 limit 1

mapper.insert:

 insert into rf_user_notice (user_notice_id, notice_id, user_notice_status, status, 
creater, create_time, modifier, modify_time, reorder, remark, enable_status, user_id, 
user_id_tmp
 ) values 

  ((select nextval('seq_rf_user_notice')), #{one.noticeId,jdbcType=INTEGER}, #{one.userNoticeStatus,jdbcType=VARCHAR}, 
  #{one.status,jdbcType=VARCHAR}, #{one.creater,jdbcType=VARCHAR}, #{one.createTime,jdbcType=TIMESTAMP}, 
  #{one.modifier,jdbcType=VARCHAR}, #{one.modifyTime,jdbcType=TIMESTAMP}, #{one.reorder,jdbcType=INTEGER}, 
  #{one.remark,jdbcType=VARCHAR}, #{one.enableStatus,jdbcType=VARCHAR}, #{one.userId,jdbcType=INTEGER}, 
  #{one.userIdTmp,jdbcType=VARCHAR}
  )

mapper.select:
  	 
  	 	insert /*+ append */ into rf_user_notice(user_notice_id,notice_id,user_id_tmp,
  	 	user_notice_status,status,creater,create_time,modifier,
  	 	modify_time,reorder,remark,enable_status,user_id) 
		select nextval('seq_rf_user_notice'),#{noticeId},'','0','1',#{creater},now(),#{modifier},now(),'1','1','1',t.user_id 
		from rf_user t where 1=1
  	
    
      and t.user_id = #{userId}	
    
    	  and t.user_mobile_phone LIKE '%' || #{userMobilePhone} || '%'
    
      and t.user_email LIKE '%' || #{userEmail} || '%'
      and t.login_time >= #{startDate}
      and t.login_time <= #{endDate}
    
