mapper.select:
SELECT count(1) as number,class_id as label from rf_fault_feedback where create_time >= to_date('${begin}','yyyy-MM-DD') and create_time < (to_date('${end}','yyyy-MM-DD')+ integer '1')
and ${where}
group by label order by number asc

SELECT count(1) as number,small_class_id as label from rf_fault_feedback where create_time >= to_date('${begin}','yyyy-MM-DD') and create_time < (to_date('${end}','yyyy-MM-DD')+ integer '1') and feedback_status in ('5','9')
and ${where}
group by label order by number asc

SELECT count(1) as number,admin_user_id as label from rf_fault_handle where create_time >= to_date('${begin}','yyyy-MM-DD') and create_time < (to_date('${end}','yyyy-MM-DD')+ integer '1')
and ${where}
group by label order by number desc

select count(1) as number,admin_user_id as label from rf_fault_handle where create_time >= to_date('${begin}','yyyy-MM-DD') and create_time < (to_date('${end}','yyyy-MM-DD')+ integer '1')
and ${where}
group by label order by number desc

select CEIL(EXTRACT( EPOCH FROM ((SUM(diff)/count(1))))/60) as number,admin_user_id as label from ( select admin_user_id,modify_time-create_time as diff from rf_fault_handle where is_solve='1' and modify_time is not null and create_time >= to_date('${begin}','yyyy-MM-DD') and create_time < (to_date('${end}','yyyy-MM-DD')+ integer '1')
and ${where}
) t group by label
