and ${where}
group by label order by number asc

select count(1) as number,label as label from(SELECT distinct user_id ,to_char(date_trunc('${type}',login_time), '${formart}') as label from sys_login_log where login_time >=to_date('${begin}','yyyy-MM-DD') and login_time < (to_date('${end}','yyyy-MM-DD')+ integer '1') and login_time is not null and client_type='' or client_type is null ) t
and ${where}
group by label order by number asc

SELECT count(1) as number , login_time as label from sys_login_log where login_time is not NULL and login_time >= to_date('${begin}','yyyy-MM-DD') and login_time < (to_date('${end}','yyyy-MM-DD')+ integer '1')
and ${where}
group by label order by number asc
