mapper.select:

select 

  ${one}

from tk_task




  order by ${example.orderByClause}

offset 0 limit 1


	    select count(1) from tk_task
	    where task_code=#{taskCode} 
	    and status=#{status} 
	    and enable_status=#{enableStatus}
	    and ((task_start_time<=#{taskStartTime} and task_end_time>=#{taskStartTime}) or (task_start_time<=#{taskEndTime} and task_end_time>=#{taskEndTime}))


    	 and task_id <> #{notEqualTaskId} 

   	 and label_id = #{labelId}
